import { EventEmitter } from '@angular/core';
import { WebComponentDialogAPI } from '@universal-robots/contribution-api';
/**
 * Custom Dialog API.
 *
 * Custom Dialogs should implement this interface.
 *
 * For custom dialogs that requires a custom footer, use {@link CustomFooterDialogComponent}.
 *
 * All 'EventEmitters' are events that can be emitted by the component.
 *
 * All other properties are provided as input properties to the component.
 */
export interface WebComponentDialogComponent<InputType = unknown, OutputType = unknown> {
    /**
     * The input data provided to the dialog when opened with
     * dialogService.openCustomDialog<T>, where T is the type of data
     */
    inputData: InputType;
    /**
     * An event emitted by the custom dialog whenever any of the output
     * data values change.
     * The outputData only gets returned to the spawning component when
     * the dialog is closed with the 'Save' button.
     */
    outputDataChange: EventEmitter<OutputType>;
    /**
     * Sets whether the dialog is allowed to be closed and saved.
     * Can be used to disallow saving when there are invalid inputs
     * in the dialog.
     */
    canSave: EventEmitter<boolean>;
    /**
     * API for working with the rest of the application from within the web-component dialog
     */
    presenterApi?: WebComponentDialogAPI;
}
