import { Observable } from 'rxjs';
import { QoSProfile } from './qos-profile';
import { Ros2HeaderType } from './ros2-client';
/**
 * Service API for ROS2 communication
 */
export interface Ros2ServiceApi {
    /**
     * Get local ROS2 robot namespace
     * @return Promise containing local ROS2 robot namespace
     */
    getRos2Namespace(): Promise<string>;
    /**
     * Subscribe to topicPath with given message type using QoS profile
     * @param connectionId Id for subscription
     * @param messageType
     * @param topic
     * @param qosProfile optional quality of service profile to apply to the subscription.
     * @return Observable to subscribe to
     */
    subscribe<T extends Ros2HeaderType>(connectionId: string, messageType: string, topic: string, qosProfile?: QoSProfile): Observable<T>;
    /**
     * Subscribe to frame transformation
     * @param connectionId Id for subscription
     * @param targetFrame
     * @param sourceFrame
     * @param publishRate in milliseconds
     * @return Observable to subscribe to
     */
    subscribeTransform<T extends Ros2HeaderType>(connectionId: string, targetFrame: string, sourceFrame: string, publishRate?: number): Observable<T>;
    /**
     * Call service to set argumentValues of service type on service name
     * @param connectionId Id for the subscription to unsubscribe
     * @param serviceType
     * @param serviceName
     * @param argumentValues
     * @param qosProfile optional quality of service profile to apply to the service client.
     * @param timeout optional float value in seconds.
     * @return Observable to subscribe to
     */
    callService<T>(connectionId: string, serviceType: string, serviceName: string, argumentValues: unknown, qosProfile?: QoSProfile, timeout?: number): Observable<T>;
}
