import { ScriptBuilder } from '../../scriptBuilder';
import { ValidationResponse } from '../common';
import { OperatorScreen } from '../nodes';
import { OptionalPromise } from './optional.promise';
/**
 * OperatorScreenFactory is a function that returns an instance of the operator screen type
 */
export type OperatorScreenFactory = () => OptionalPromise<OperatorScreen>;
/**
 * A function that can be used to create a script builder for operator screen code
 */
export type OperatorScreenCodeGenerator = (operatorScreen: OperatorScreen) => OptionalPromise<ScriptBuilder>;
/**
 * Validator function that takes an operator screen instance and validates it
 * @param operatorScreen An OperatorScreen to validate
 * @returns
 */
export type OperatorScreenValidator = (operatorScreen: OperatorScreen) => OptionalPromise<ValidationResponse>;
/**
 * A function that can be used to move between versions of an OperatorScreen
 */
export type OperatorScreenVersionController = (loadedOperatorScreen: OperatorScreen) => OptionalPromise<OperatorScreen>;
/**
 * OperatorScreenBehaviors describes the possible behaviors defined by operator screens.
 * The object will be registered by calling {@link registerOperatorScreenBehavior}
 *
 */
export type OperatorScreenBehaviors = {
    factory: OperatorScreenFactory;
    generatePreamble?: OperatorScreenCodeGenerator;
    validator?: OperatorScreenValidator;
    upgrade?: OperatorScreenVersionController;
};
