import { SidebarItem } from '../nodes';
import { OptionalPromise } from './optional.promise';
/**
 * A function that can be used to create a Sidebar item
 */
export type ItemFactory = () => OptionalPromise<SidebarItem>;
/**
 * A function that can be used to move between versions of a Sidebar item
 * @param {SidebarItem} loadedItem The source item
 * @param {SidebarItem} defaultItem The fallback item
 * @returns {OptionalPromise<SidebarItem>} The upgraded Sidebar item
 */
export type VersionController = (loadedItem: SidebarItem, defaultItem: SidebarItem) => OptionalPromise<SidebarItem>;
/**
 * Public API specifying available behaviors for the Sidebar item
 */
export type SidebarItemBehaviors = {
    factory: ItemFactory;
    upgrade?: VersionController;
};
