import { ScriptBuilder } from '../../scriptBuilder';
import { ValidationResponse } from '../common';
import { ProgramNode, SmartSkillInstance, SmartSkillsData } from '../nodes';
import { OptionalPromise } from './optional.promise';
/**
 * The factory returns the default parameter data for a smart skill node
 *
 * @returns A SmartSkillNode containing default data
 */
export type SmartSkillsFactory = () => OptionalPromise<SmartSkillInstance>;
/**
 * CodeGenerator is a function that generates the code for the smart skill
 *
 * @param instance The configured settings for the smart skill instance
 * @returns A ScriptBuilder containing the generated script code
 */
export type SmartSkillsCodeGenerator = (instance: SmartSkillInstance) => OptionalPromise<ScriptBuilder>;
/**
 * Preamble Code Generator is a function that is run once per type of smart skill,
 * that generates code to be added to the main preamble
 *
 * @returns A ScriptBuilder containing the generated script code
 */
export type StartSmartSkillsPreambleCodeGenerator = (instance: SmartSkillInstance) => OptionalPromise<ScriptBuilder>;
/**
 * Generates a program node from the data collected by recording the RTDE data,
 * during execution of the smart skill
 *
 * @param data The RTDE data collected during execution
 * @returns A programnode to be added to the programtree if recording is active
 */
export type SmartSkillsProgramNodeGenerator = (instance: SmartSkillInstance, data: SmartSkillsData[]) => OptionalPromise<ProgramNode>;
/**
 * Validator is a function that validates the smart skill instance
 * @param instance The configured settings for the smart skill instance
 * @returns A ValidationResponse containing the validation result
 */
export type SmartSkillsValidator = (instance: SmartSkillInstance) => OptionalPromise<ValidationResponse>;
/**
 * CodeGenerator is a function that generates the code for the smart skill
 *
 * @param instance The configured settings for the smart skill instance
 * @param data The data object containing the recorded data
 * @returns A ScriptBuilder containing the generated script code
 */
export type SmartSkillsUndoRedoGenerator = (instance: SmartSkillInstance, data: SmartSkillsData[]) => OptionalPromise<ScriptBuilder>;
/**
 * SmartSkillInstanceVersionController is a function that upgrades the smart skill instance to a new version
 * @param loadedSmartSkillInstance The loaded smart skill instance
 * @returns A SmartSkillInstance containing the upgraded instance
 */
export type SmartSkillInstanceVersionController = (loadedSmartSkillInstance: SmartSkillInstance) => OptionalPromise<SmartSkillInstance>;
/**
 * SmartSkillsBehaviors describes the possible behaviors defined by a smart skill.
 * The object will be registered by calling {@link registerSmartSkillBehavior}
 *
 */
export type SmartSkillBehaviors = {
    factory: SmartSkillsFactory;
    startExecution: SmartSkillsCodeGenerator;
    stopExecution?: SmartSkillsCodeGenerator;
    generateProgramPreamble?: StartSmartSkillsPreambleCodeGenerator;
    generateInstanceProgramPreamble?: StartSmartSkillsPreambleCodeGenerator;
    createProgramNode?: SmartSkillsProgramNodeGenerator;
    validator?: SmartSkillsValidator;
    undo?: SmartSkillsUndoRedoGenerator;
    redo?: SmartSkillsUndoRedoGenerator;
    upgradeSmartSkillInstance?: SmartSkillInstanceVersionController;
};
