/**
 * Contains the allowed types for the element type
 */
export type ProgramLabelPartType = 'primary' | 'secondary';
/**
 * Contains one part of the label. A part consists of a type (primary or secondary) and a value
 */
export type ProgramLabelPart = {
    type: ProgramLabelPartType;
    value: string;
};
/**
 * Contains one part of a translated program label.
 * A part consist of a type (primary or secondary), translationKey, and optional interpolateParams (values inserted into the label)
 */
export type TranslatedProgramLabelPart = {
    type: ProgramLabelPartType;
    translationKey: string;
    interpolateParams?: Record<string, string>;
};
/**
 * Contains an array of ProgramLabelPart
 */
export type AdvancedProgramLabel = ProgramLabelPart[];
/**
 * Contains an array of TranslatedProgramLabelPart
 */
export type AdvancedTranslatedProgramLabel = TranslatedProgramLabelPart[];
/**
 * Contains either a AdvancedProgramLabel, a AdvancedTranslatedProgramLabel or a string
 */
export type ProgramLabel = AdvancedProgramLabel | AdvancedTranslatedProgramLabel | string;
