import { ApplicationNode, Frame } from '../nodes';
/**
 * @deprecated Please use the ApplicationService from the API to get information on Application Nodes
 * @example
 * // in program presenter
 * this.presenterAPI.applicationService.getApplicationNode(ApplicationNodeType.FRAMES)
 * // in program behavior
 * const api = new ProgramAPI(self);
 * api.applicationService.getApplicationNode(ApplicationNodeType.FRAMES)
 * // in application behavior
 * const api = new ApplicationAPI(self);
 * api.applicationService.getApplicationNode(ApplicationNodeType.FRAMES)
 */
export interface ApplicationContext {
    readonly contributions: ContributionContext;
    readonly frames: FramesContext;
}
/**
 * @deprecated See {@link ApplicationContext}
 */
export interface ContributionContext {
    readonly contributionList: Array<ContributionDefinition>;
    /**
     * Returns an ApplicationNode or undefined if the node doesn't exist
     * @param type type of contribution node to fetch
     */
    getContribution(type: string): ApplicationNode | undefined;
}
/**
 @deprecated See {@link ApplicationContext}
 */
export interface ContributionDefinition extends ApplicationNode {
    parentType: string;
}
/**
 @deprecated See {@link ApplicationContext}
 */
export interface FramesContext {
    readonly framesList: Array<Frame>;
    /**
     * Returns a Frame or undefined if the frame doesn't exist
     * @param frameId frameId of the frame to fetch
     */
    getFrame(frameId: string): Frame | undefined;
}
