import { ProgramNode } from '../nodes';
import { SubtreeNode } from './subtreeNode';
/**
 * TreeContext provides access to a program nodes ancestors, children, and siblings
 */
export interface TreeContext {
    ancestors: AsyncIterableIterator<Readonly<ProgramNode>>;
    children: AsyncIterableIterator<Readonly<SubtreeNode>>;
    depthFirst: AsyncIterableIterator<Readonly<SubtreeNode>>;
    previousSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
    nextSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
}
/**
 * ValidationContext provides context for validation
 */
export interface ValidationContext {
    traverse: TreeTraverse;
}
/**
 * ScriptContext provides context for script generation
 */
export interface ScriptContext {
    traverse: TreeTraverse;
}
/**
 * Provides context for program node insertion
 */
export interface InsertionContext {
    traverse: InsertionTraverse;
}
/**
 * TreeContext provides access to a program nodes ancestors, children, and siblings
 */
export interface TreeTraverse {
    ancestors: AsyncIterableIterator<Readonly<ProgramNode>>;
    children: AsyncIterableIterator<Readonly<ProgramNode>>;
    depthFirst: AsyncIterableIterator<Readonly<ProgramNode>>;
    previousSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
    nextSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
}
/**
 * TreeContext provides access to a program nodes ancestors, and siblings
 */
export interface InsertionTraverse {
    ancestors: AsyncIterableIterator<Readonly<ProgramNode>>;
    previousSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
    nextSiblings: AsyncIterableIterator<Readonly<ProgramNode>>;
}
/**
 * A program tree traversal where SubtreeNode is returned.
 */
export type SubtreeContext = BaseTraverse<SubtreeNode, SubtreeNode>;
/**
 * A base traversal
 */
export interface BaseTraverse<N, C> {
    ancestors: AsyncIterableIterator<Readonly<N>>;
    children: AsyncIterableIterator<Readonly<C>>;
    depthFirst: AsyncIterableIterator<Readonly<C>>;
    previousSiblings: AsyncIterableIterator<Readonly<N>>;
    nextSiblings: AsyncIterableIterator<Readonly<N>>;
}
