/**
 * Describes information required to set a label on a signal
 */
import { SignalAnalogDomainValueEnum, SignalBooleanValue, SignalFloatValue } from '../sources';
import { DigitalInPreset } from '../sources';
/**
 * Describes information required to update a label for a signal
 */
export interface SetLabel {
    readonly sourceID: string;
    readonly signalID: string;
    readonly label: string;
}
/**
 * Describes information required to update an analog domain for a signal
 */
export interface UpdateAnalogDomainValue {
    readonly sourceID: string;
    readonly signalID: string;
    readonly analogDomain: SignalAnalogDomainValueEnum;
}
/**
 * Describes information required to update a digital-in preset
 */
export interface UpdateDigitalInPreset {
    readonly sourceID: string;
    readonly signalID: string;
    readonly preset: DigitalInPreset;
}
/**
 * Describes information required to update a signal value
 */
export interface UpdateSignalValue {
    readonly sourceGroupID?: string;
    readonly sourceID: string;
    readonly signalID: string;
    readonly value: SignalBooleanValue | SignalFloatValue;
}
