import { BranchNode, ProgramNode } from '../nodes';
/**
 * Describes a node in a subtree
 */
export interface SubtreeNode {
    readonly id: string;
    readonly node: Readonly<ProgramNode | BranchNode>;
}
/**
 * Describes a relative insertion point
 */
export declare enum InsertionEnum {
    BEFORE = "before",
    AFTER = "after",
    INTO_FIRST = "intoFirst",
    INTO_LAST = "intoLast"
}
/**
 *
 */
export declare enum BlockEnum {
    MAIN_PROGRAM = "mainProgram",
    BEFORE_START = "beforeStart"
}
/**
 * Describes information required to add a node to the tree
 * @property {boolean | undefined} changeSelection -
 * if true or undefined, the selection will be changed to the newly added node, if false the selection will remain unchanged
 */
export interface AddNode {
    readonly insertionRelativeToPivotNode: InsertionEnum;
    readonly pivotNodeId: string;
    readonly node: Readonly<ProgramNode | BranchNode>;
    readonly changeSelection?: boolean;
}
/**
 * Describes information required to add a node to the root of a program block
 * @property insertionRelativeToBlock Which position to insert. Either first or last
 * @property block Where to insert. Either the main program or the before start
 * @property node The node to insert
 * @property changeSelection Whether to change the selected node after the add. If true or undefined, the
 * selection will be changed to the newly added node, if false the selection will remain unchanged
 */
export interface AddBlockChildNode {
    readonly insertionRelativeToBlock: InsertionEnum.INTO_FIRST | InsertionEnum.INTO_LAST;
    readonly block: BlockEnum;
    readonly node: Readonly<ProgramNode | BranchNode>;
    readonly changeSelection?: boolean;
}
/**
 * Describes information required to remove a node from the tree
 * @property {boolean | undefined} changeSelection -
 * if true or undefined, the selection will be changed to the older sibling/parent of the deleted node,
 * if false the selection will remain unchanged
 */
export interface RemoveNode {
    readonly nodeId: string;
    readonly changeSelection?: boolean;
}
