import { ProgramNode } from '../..';
import { NodeType } from './program-node-type';
export interface ScriptNode extends ProgramNode {
    type: NodeType.SCRIPT;
    parameters: {
        /**
         * @deprecated This field is no longer used
         */
        name: string;
        /**
         * The filename of the referenced urscript. Will only be used if expression is undefined
         */
        fileName?: string;
        /**
         * The urscript
         */
        expression?: string;
        /**
         * If the expression urscript is valid
         */
        valid?: boolean;
    };
}
