import { NodeType } from '.';
import { KinematicPosition, ProgramNode, URVariable } from '../..';
import { TabInputModel } from '../../../common';
import { Acceleration, AngularAcceleration, AngularSpeed, Speed, Time } from '../../../values';
export interface WaypointNode extends ProgramNode {
    type: NodeType.WAYPOINT;
    parameters: {
        activeParameterType: 'Time' | 'Inherited' | 'Custom';
        time: TabInputModel<Time>;
        custom: {
            joint: {
                speed: TabInputModel<AngularSpeed>;
                acceleration: TabInputModel<AngularAcceleration>;
            };
            linear: {
                speed: TabInputModel<Speed>;
                acceleration: TabInputModel<Acceleration>;
            };
        };
        frame: {
            custom: boolean;
            frameId: string;
        };
        positionType: 'fixed' | 'expression' | 'variable';
        fixed?: {
            nameVariable: URVariable;
            position?: KinematicPosition;
        };
        expression?: {
            expression: string;
        };
        variable?: {
            variable: URVariable;
        };
        transformVariable?: URVariable;
        transform: boolean;
    };
}
