import { Angle, Acceleration } from '../../../values';
/**
 * Interface describing mounting which has a base angle and a tilt angle.
 */
export interface Mounting {
    baseAngle: Angle;
    tiltAngle: Angle;
}
/**
 * Interface describing the amount of gravity acting in each direction on the robot
 */
export interface Gravity {
    gx: Acceleration;
    gy: Acceleration;
    gz: Acceleration;
}
