import { URFunction, URModule, URSymbol } from '../../ur-symbol';
/**
 * Defines a configuration item
 */
export interface ConfigurationItem {
    nodeGuid: string;
    description: string;
    type: 'Reference' | 'Value';
    symbol: URSymbol;
}
/**
 * Defines a reference item
 */
export interface ReferenceItem extends ConfigurationItem {
    type: 'Reference';
    options: Array<ReferenceOption>;
    selectedOption?: ReferenceOption;
}
/**
 * Defines a reference option
 */
export interface ReferenceOption {
    module?: URModule;
    function?: URFunction;
    label: string;
}
/**
 * Defines a value item
 */
export interface ValueItem extends ConfigurationItem {
    type: 'Value';
    value: any;
    min?: number;
    max?: number;
}
/**
 * Checks if the value implements ReferenceItem
 */
export declare const isReferenceItem: (value: ConfigurationItem) => value is ReferenceItem;
/**
 * Checks if the value implements ValueItem
 */
export declare const isValueItem: (value: ConfigurationItem) => value is ValueItem;
