/**
 * JointPositions defines a position of the robot, specified
 * by the angle of the joints.
 */
export interface JointPositions {
    base: number;
    shoulder: number;
    elbow: number;
    wrist1: number;
    wrist2: number;
    wrist3: number;
}
/**
 * A type guard to check if an object is a JointPositions
 */
export declare function isJointPositions(jp: any): jp is JointPositions;
