import { KinematicInfo } from './link.model';
import { Pose } from './pose.model';
/**
 * A KinematicPosition defines a position of the robot, including the kinematics
 * data from the robot where it was taught, so it can be accurately
 * reproduced on another robot.
 * @deprecated
 */
export interface KinematicPosition {
    jointVector: Array<number>;
    links: KinematicInfo;
    tcp: Pose;
    frame?: Pose;
}
/**
 * A type guard to check if an object is a KinematicPosition
 */
export declare function iskinematicPosition(kinematicPosition: any): kinematicPosition is KinematicPosition;
/**
 * A Position defines a pose of the robot together with the kinematicPosition
 * defining that pose.
 * @deprecated
 */
export interface Position {
    kinematicPosition: KinematicPosition;
    pose: Pose;
}
