import { URVariable, VariableValueType } from '../../ur-symbol';
/**
 * Defines a StatusOption
 */
export interface StatusType {
    description: string;
    variable?: URVariable;
    initialValue: any;
}
/**
 * Union type of used VariableValueTypes used in status nodes
 */
type ValueTypeUnion = VariableValueType.BOOLEAN | VariableValueType.FLOAT | VariableValueType.INTEGER | VariableValueType.STRING;
