import { FrameName, TCPName } from '../application';
import { JointPositions, Pose } from './index';
/**
 * A Waypoint defines a position of the robot, with a reference to the frame that it's will be relative to.
 */
export type Waypoint = {
    frame: FrameName;
    pose: Pose;
    qNear: JointPositions;
    tcp?: TCPName;
};
/**
 * A type guard to check if an object is a Waypoint
 */
export declare function isWaypoint(waypoint: any): waypoint is Waypoint;
