/**
 * Describes an instance of a smart skill, with its own set of configuration parameters
 */
export interface SmartSkillInstance {
    name: string;
    type: string;
    version?: string;
    enabled?: boolean;
    /**
     * The frequency at which the recording should be taken, in Hertz (Hz).
     * This parameter is optional and can be set to customize the recording interval.
     * Must be between 1 and 500 Hz. Defaults to 100 Hz if not specified or out of range.
     */
    recordingFrequency?: number;
    /**
     * An array of RTDE (Real-Time Data Exchange) signal names to be recorded during the smart skill execution.
     * This parameter is optional and can be used to specify which signals to capture.
     * Defaults to ['timestamp', 'target_q', 'actual_TCP_pose', 'target_TCP_pose', 'robot_status_bits', 'tcp_offset'].
     * Note: 'target_q' will always be recorded.
     */
    recordingSignals?: string[];
    parameters?: {
        [key: string]: any;
    };
}
