import { ProgramPresenterAPI } from '../../api-classes';
import { RobotSettings } from '../common';
import { ApplicationContext, TreeContext } from '../context';
import { ProgramNode } from '../nodes';
/**
 * Program Presenter API
 *
 * All other properties are provided as input properties to the component.
 */
export interface ProgramPresenter {
    /**
     * Current settings for the robot
     */
    robotSettings?: RobotSettings;
    /**
     * Contributed node
     */
    contributedNode?: ProgramNode;
    /**
     * A set of iterators to use
     * for traversing the program tree.
     * @deprecated Will be replaced by {@link ProgramTreeService.getTreeContextFromProgramNode} method on the {@link ProgramPresenterAPI}. For now that method is not implemented.
     */
    programTree?: TreeContext;
    /**
     * @deprecated Please use the ApplicationAPI
     * Application Context
     */
    applicationContext?: ApplicationContext;
    /**
     * API for working with program nodes
     */
    presenterAPI?: ProgramPresenterAPI;
}
