import type { SignalEvent } from './SignalEvent';
/**
 * Event indicating a change in an analog domain
 */
export interface SignalAnalogDomain extends SignalEvent {
    /**
     * New analog domain value
     */
    value: SignalAnalogDomainValueEnum;
}
/**
 * Enum with all possible analog domain values
 */
export declare enum SignalAnalogDomainValueEnum {
    CURRENT = "CURRENT",
    VOLTAGE = "VOLTAGE"
}
