/**
 * Maintains a reference to the local storage key and offers functionality to add, retrieve, and remove items from the local storage list.
 */
export declare class LocalStorageList<T> {
    private localStorageKey;
    constructor(key: string);
    /**
     * Save data in local storage
     * @param item - stringable data to save in local storage
     */
    add(data: T | Array<T>): void;
    /**
     * Remove data from local storage
     * @param item
     */
    remove(item: T): void;
    /**
     * Get latest added item from local storage. Be aware: this function does not mutate local storage
     * @returns reference to latest added item from local storage
     */
    getLatest(): string | undefined;
    /**
     * Get the first item from local storage. Be aware: this function does not mutate local storage
     * @returns reference to the first added item from local storage
     */
    getFirst(): string | undefined;
    /**
     * Clear local storage with the current key
     */
    clear: () => void;
    length(): number;
}
