#!/usr/bin/env node

const urserviceHelper = require("./lib/urservice-helper");

function parseArguments() {
  const args = process.argv.slice(2);
  const vendorId = args[0];
  const urcapId = args[1];
  return { vendorId, urcapId };
}

const { vendorId, urcapId } = parseArguments();

urserviceHelper.deleteUrcap(vendorId, urcapId).then(
  (value) => console.log(value),
  (reason) => console.log(reason),
);
